{
    function pad(str, size, car) {
        while (str.length < size) str = car + str;
        return str;
    }

    function duration_mm_ss(time) {
        let time_m = Math.floor(time / 60);
        let time_s = time - time_m * 60;
        return pad(pad(time_m.toString(), 2, '0'), 3, ' ') + ":" + pad(time_s.toString(), 2, '0')
    }

    function get_pwrZones(power, zones) {
        for (let i = 0; i < zones.length; i++) {
            if (power <= zones[i]) return i+1;
        }
        return -1;
    }

    let activity = icu.activity;    
    let streams = icu.streams;
    let pwr = streams.get("fixed_watts").data;
    let d = streams.get("time").data;

    let pwrMin = 0;
    let pwrMax = 0;
    for (let i = 0; i < pwr.length; i++) {
        if (pwrMax < pwr[i]) {
            pwrMax = pwr[i];
        }
    }

    //  Athlete power zones
    // export const ZoneColours = ["#009e80", "#009e00", "#ffcb0e", "#ff7f0e", "#dd0447", "#6633cc", "#504861" ]
    // export const ZoneColours4 = ["#009e00", "#ffcb0e", "#ff7f0e", "#dd0447" ]
    // export const ZoneColours3 = ["#009e00", "#ffcb0e", "#dd0447" ]

    // export function toZoneColours(zc) {
    //    if (zc >= 5) return ZoneColours
    //    if (zc >= 4) return ZoneColours4
    //    return ZoneColours3
    // }

    colors = ["#009e80", "#009e00", "#ffcb0e", "#ff7f0e", "#dd0447", "#6633cc", "#504861"]
    zonesP = []
    for (let i=0; i < activity.icu_power_zones.length; i++) {
        zonesP.push(Math.min(pwrMax, Math.floor(activity.icu_ftp * activity.icu_power_zones[i] / 100)));
    }

    //  Histogram
    let totalTime = 0;
    let watts_histo = [];   
    for (let i = 0; i <= pwrMax; i++) watts_histo.push(0);
    for (let i = 0; i < pwr.length; i++) {
        if (pwr[i] > pwrMin) {
            watts_histo[pwr[i]] += 1;
            totalTime += 1;
        }
    }

    //  Accumulation
    let cumTime = 0;
    let data_Pwr = [];
    let data_Time = [];
    let data_TimeStr = [];
    let data_Text = [];

    for (let i = pwrMin; i < watts_histo.length; i++) {
        cumTime += watts_histo[i];

        data_Pwr.push(i);
        data_Time.push(cumTime);
        data_TimeStr.push(duration_mm_ss(cumTime));

        let percentUnder = cumTime / totalTime * 100;      
        let percentOver = 100 - cumTime / totalTime * 100;

        let str =   "<b>(Z" + get_pwrZones(i, zonesP) + ") " + i + " watts</b>"
                +   "<br>" + duration_mm_ss(cumTime)             + " Under (" + percentUnder.toFixed(1) + " %)"
                +   "<br>" + duration_mm_ss(totalTime - cumTime) + " Over  (" + percentOver.toFixed(1) + " %)"
        ;

        data_Text.push(str);
    }

    //  Shapes for power zones
    shapesP = [{
        type: 'rect',
        xref: 'x',
        yref: 'y',
        x0: 0,
        y0: 0,
        x1: totalTime,
        y1: zonesP[0],
        layer: 'below',
        opacity: 0.2,
        fillcolor: colors[0],
        line: {
            color: 'black',
            width: 0,
        dash: 'dot'
        }
    }];

    for (let i=0; i < activity.icu_power_zones.length-1; i++) {
        temp_shape = {
            type: 'rect',
            xref: 'x',
            yref: 'y',
            x0: 0,
            y0: zonesP[i]+1,
            x1: totalTime,
            y1: zonesP[i+1],
            layer: 'below',
            opacity: 0.2,
            fillcolor: colors[i+1],
            line: { width: 0 }
        }
        shapesP.push(temp_shape)
    }

    //  Trace
    trace1 = {
        x: data_Time,
        y: data_Pwr,
        text: data_Text,
        type: 'scatter',
        yaxis: 'y',
        xaxis: 'x',
        name: 'Time under/over power',
        mode: 'lines',
        hoverinfo: 'text',
        line: { color: 'black' }
    };

    var data = [trace1];

    //  Layout    
    layout = {
        title: {
            text: "Time under/over power",
            font: { color: '#999999' },
        },
        xaxis: {
            side: 'bottom',
            range: [0, cumTime],
            showline: true, zeroline: true,
            autotick: false, tick0: 0, dtick: cumTime/10,
            showspikes: true, spikemode: 'across', spikesnap: "cursor+data", spikedash: 'solid', spikethickness: 1, spikecolor:  '#999999',
            color: '#999999',
        },
        yaxis: {
            side: 'left',            
            range: [pwrMin, pwrMax+1],
            showline: true, zeroline: true,
            autotick: false, tick0: 0, dtick: 50,
            showspikes: true, spikemode: 'across', spikesnap: "cursor+data", spikedash: 'solid', spikethickness: 1, spikecolor:  '#999999',
            title: { text: 'Power', },
            color: '#999999',
        },
        margin: { l: 45, r: 20, t: 30, b: 20 },
        hovermode: 'x unified',
        spikedistance: -1,
        shapes: shapesP,
        showlegend: false,
        legend: {"orientation": "h"}
    }

    chart = { data, layout };
}

var data = []
var colors = ['rgb(0, 158, 128)', 'rgb(0, 158, 0)', 'rgb(255, 203, 14)', 'rgb(255, 127, 14)', 'rgb(221, 4, 71)', 'rgb(102, 51, 204)', 'rgb(80, 72, 97)', 'rgb(255, 160, 14)']
activity = icu.activity
intervals = activity.icu_zone_times || []
for (let i = 0; i < intervals.length - 1; i++) {
  let iv = intervals[i];
  const date = new Date(null);
date.setSeconds(iv.secs); 
const result = date.toISOString().slice(11, 19);
  data.push({
    x: [iv.secs],
    y: [1],
orientation: 'h',
    name: iv.id,
    type: 'bar',
    marker: { color: colors[i], width: 5},
    text: iv.id,
textposition: 'auto',
hovertemplate: `${result}<br>${Math.round(iv.secs / activity.moving_time * 100)}%`,
hoverinfo:"x",
insidetextanchor: "middle"
  });
}

var layout = {
height: 80,
  title: {text:'Time in Zones'},
margin:{b:0, l:0, r:0, t:20},
  barmode: 'stack',
hoverlabel: {align: 'left'},
hovermode: 'x',
showlegend: false,
  yaxis : { showgrid : false, visible: false },
  xaxis: { zeroline : false, showgrid : false,  range: [0, activity.moving_time], visible: false}
};

chart = {data, layout}
